(NSArray *) getBuddyListWithArtsmeshUserNameList:(NSArray*)artsmeshUserNameList{	NSMutableArray * arr=[[NSMutableArray alloc] initWithCapacity:[artsmeshUserNameList count]];		NSString * name=nil;	for(name in artsmeshUserNameList)	{		iChatBuddy * buddy=[ChatTaskHelper getBuddyWithArtsmeshUserName:name];		if (buddy!=nil) {			[arr addObject:buddy];		}	}	return arr;}+(void) startChat:(NSArray*) buddyList		 chatType:(ChatType)chatType{	if ([buddyList count]>0) {				iChatService *svc=[ChatTaskHelper artsmeshiChatService];				NSString * className=nil;		switch (chatType) {			case TextChat:				className=@"text chat";				break;							case AudioChat:				className=@"audio chat";				break;							case VideoChat:				className=@"video chat";				break;							default:				className=@"text chat";		}				/* Get the class for the text chat AppleScript class */		Class videoChatClass = [_iChatApp classForScriptingClass:className];				/* Go to the chat room named "iChatStatus" on the AIM service we found */		NSDictionary *chatProperties = [NSDictionary dictionaryWithObjectsAndKeys:svc, @"service", 										buddyList, @"participants", nil];				[className release];				/* Create the text chat object */		iChatVideoChat * myChat =[[videoChatClass alloc] initWithProperties:chatProperties];				/* Add the text chat to the app list of open chats */		[[_iChatApp videoChats] addObject:myChat];        		/* iChat will now send out the request to create chat room (not synchronous) */			}	}@end